            + "		inner join tb_movinv m ON f.F_ClaDoc = m.F_DocMov AND f.F_ClaPro = m.F_ProMov AND f.F_Lote = m.F_LotMov"
            + "		where  m.F_FecMov = ? AND m.F_ConMov NOT IN ( 1000, 1001 )"
            + "		order by f.F_IdFact asc"
            + "		limit 1)"
            + "	GROUP BY f.F_ClaDoc, f.F_Proyecto, f.F_Lote"
            + ") fact ON fact.factura = m.F_DocMov AND fact.clave = m.F_ProMov AND fact.folLot = m.F_LotMov "
            + "LEFT JOIN ("
            + "	SELECT d.F_DocDev AS devolucion, d.F_DocRef AS factura, f.F_Lote AS folLot, f.F_ClaPro AS clave, f.F_Proyecto AS proyecto, u.F_NomCli AS unidad, d.F_Obs AS coment "
            + "	FROM tb_factura f "
            + "	INNER JOIN tb_devoluciones d ON DATE( d.F_FecDev ) = ? AND f.F_ClaDoc = d.F_DocRef AND f.F_Proyecto = d.F_Proyecto "
            + "	INNER JOIN tb_uniatn u ON u.F_ClaCli = f.F_ClaCli"
            + "	WHERE f.F_IdFact >= (SELECT f.F_IdFact"
            + "		FROM tb_factura f "
            + "		inner join tb_movinv m ON f.F_ClaDoc = m.F_DocMov AND f.F_ClaPro = m.F_ProMov AND f.F_Lote = m.F_LotMov"
            + "		where  m.F_FecMov = ? AND m.F_ConMov NOT IN ( 1000, 1001 )"
            + "		order by f.F_IdFact asc"
            + "		limit 1)"
            + "	GROUP BY f.F_ClaDoc, d.F_IdDev, f.F_Proyecto, f.F_Lote "
            + ") dev ON dev.devolucion = m.F_DocMov AND dev.clave = m.F_ProMov AND dev.folLot = m.F_LotMov "
            + "WHERE m.F_FecMov = ? AND m.F_ConMov NOT IN ( 1000, 1001 )"
            + "GROUP BY m.F_IdMov ORDER BY m.F_IdMov;";
