    public static final String OBTENER_KARDEX_REDISTRIBUCION_POR_FECHA = "SELECT m.F_User AS usuario, c.F_DesCon AS concepto, m.F_ProMov AS clave, l.F_ClaLot AS lote, DATE_FORMAT( l.F_FecCad, '%d/%m/%Y') AS caducidad,( m.F_CantMov * m.F_SigMov ) AS cantidad, m.F_CostMov AS costo, u.F_DesUbi AS ubicacion, DATE_FORMAT( m.F_FecMov, '%d/%m/%Y' ) AS fechaMovimiento, m.F_hora AS hora, m.F_DocMov AS documento, com.F_FolRemi AS remision, m.F_IdMov AS idMovimiento, l.F_Proyecto AS proyecto, p.F_DesProy AS descProyecto, o.F_DesOri AS origen, IFNULL(m.F_Comentarios,'') as comentarios FROM tb_movinv m INNER JOIN tb_coninv c ON m.F_ConMov = c.F_IdCon INNER JOIN tb_ubica u ON m.F_UbiMov = u.F_ClaUbi INNER JOIN tb_lote l ON m.F_LotMov = l.F_FolLot AND m.F_ProMov = l.F_ClaPro AND m.F_UbiMov = l.F_Ubica LEFT JOIN tb_compra com ON l.F_FolLot = com.F_Lote  LEFT JOIN tb_origen o ON o.F_ClaOri = l.F_Origen LEFT JOIN tb_proyectos p ON l.F_Proyecto = p.F_Id WHERE m.F_FecMov =? AND ( m.F_ConMov = 1000 OR m.F_ConMov = 1 OR m.F_ConMov = 1001) GROUP BY m.F_IdMov ORDER BY m.F_IdMov;";

