    public static final String OBTENER_KARDEX_POR_FECHA_EXPORTAR = "SELECT m.F_IdMov AS noMov, m.F_User AS usuario,IF( m.F_ConMov = 1, com.ori, '' ) AS ori,IF( m.F_ConMov = 1, com.remision, '' ) AS remision, IF( m.F_ConMov = 1, com.proveedor, '' ) AS proveedor, IFNULL(CASE    m.F_ConMov WHEN 51 THEN    fact.factura WHEN 3 THEN fact.factura WHEN 4 THEN dev.factura WHEN 5 THEN dev.factura ELSE m.F_DocMov END,'') AS folioSalida, IFNULL(CASE m.F_ConMov WHEN 3 THEN fact.factura WHEN 4 THEN m.F_DocMov WHEN 5 THEN m.F_DocMov ELSE '' END,'') AS folioReferencia, IFNULL(CASE m.F_ConMov WHEN 51 THEN fact.unidad WHEN 3 THEN fact.unidad WHEN 4 THEN dev.unidad WHEN 5 THEN dev.unidad ELSE 'CEDIS CENTRAL' END,'') AS puntoEntrega, c.F_DesCon AS concepto,    m.F_ProMov AS clave,    l.F_ClaLot AS lote, SUBSTRING(mm.F_DesPro ,20) AS descripcion, DATE_FORMAT( l.F_FecCad, '%d/%m/%Y' ) AS caducidad,(m.F_CantMov * m.F_SigMov ) AS cantidadMovimiento, u.F_DesUbi AS ubicacion, o.F_DesOri AS origen, p.F_DesProy AS proyecto, DATE_FORMAT( m.F_FecMov, '%d/%m/%Y' ) AS fechaMovimiento, IFNULL(CASE m.F_ConMov WHEN 51 THEN DATE_FORMAT( fact.F_FecEnt, '%d/%m/%Y' ) ELSE '' END,'') AS fechaEnt, m.F_hora AS hora, IFNULL(CASE    m.F_ConMov WHEN 1 THEN    com.obcom     WHEN 3 THEN    'Folio Cancelado'     WHEN 4 THEN    dev.coment     WHEN 5 THEN    dev.coment     WHEN 51 THEN    fact.Comen ELSE '' END,'') AS comentarios, CASE WHEN com.ordSuministro = 'NULL' THEN  '' ELSE IFNULL(com.ordSuministro, '') END AS ordSuministro FROM tb_movinv m  "
            + "INNER JOIN tb_coninv c ON m.F_ConMov = c.F_IdCon "
            + "INNER JOIN tb_ubica u ON m.F_UbiMov = u.F_ClaUbi "
            + "INNER JOIN tb_lote l ON m.F_LotMov = l.F_FolLot "
            + "INNER JOIN tb_medica mm ON mm.F_ClaPro = m.F_ProMov AND m.F_ProMov = l.F_ClaPro AND m.F_UbiMov = l.F_Ubica "
            + "LEFT JOIN tb_proyectos p ON l.F_Proyecto = p.F_Id LEFT JOIN tb_origen o ON o.F_ClaOri = l.F_Origen "
            + "LEFT JOIN (SELECT c.F_ClaDoc AS documento,c.F_OrdCom AS ori,c.F_FolRemi AS remision,c.F_Lote AS folLot,c.F_ClaPro AS clave,    p.F_NomPro AS proveedor,c.F_Obser AS obcom, c.F_OrdenSuministro AS ordSuministro FROM    tb_compra c INNER JOIN tb_proveedor p ON p.F_ClaProve = c.F_ProVee WHERE c.F_FecApl = ? GROUP BY c.F_ClaDoc,c.F_FolRemi,c.F_Lote) com ON com.documento = m.F_DocMov AND com.clave = m.F_ProMov AND com.folLot = m.F_LotMov \n"
            + "LEFT JOIN (SELECT f.F_ClaDoc AS factura, f.F_Lote AS folLot, f.F_ClaPro AS clave, f.F_Proyecto AS proyecto, u.F_NomCli AS unidad, ob.F_Obser AS Comen,    f.F_FecEnt FROM    tb_factura f INNER JOIN tb_uniatn u ON u.F_ClaCli = f.F_ClaCli INNER JOIN tb_obserfact ob ON ob.F_IdFact = f.F_ClaDoc AND ob.F_Proyecto = f.F_Proyecto  GROUP BY f.F_ClaDoc, f.F_Proyecto, f.F_Lote) fact ON fact.factura = m.F_DocMov AND fact.clave = m.F_ProMov AND fact.folLot = m.F_LotMov \n"
            + "LEFT JOIN (SELECT d.F_DocDev AS devolucion, d.F_DocRef AS factura, f.F_Lote AS folLot,    f.F_ClaPro AS clave, f.F_Proyecto AS proyecto, u.F_NomCli AS unidad, d.F_Obs AS coment FROM tb_factura f INNER JOIN tb_devoluciones d ON DATE( d.F_FecDev ) = ? AND f.F_ClaDoc = d.F_DocRef INNER JOIN tb_uniatn u ON u.F_ClaCli = f.F_ClaCli GROUP BY    f.F_ClaDoc, d.F_IdDev, f.F_Proyecto, f.F_Lote) dev ON dev.devolucion = m.F_DocMov AND dev.clave = m.F_ProMov AND dev.folLot = m.F_LotMov WHERE    m.F_FecMov = ? AND m.F_ConMov NOT IN ( 1000, 1001 ) GROUP BY m.F_IdMov ORDER BY    m.F_IdMov; ";

